import { useState } from "react";
import { useLocation } from "react-router-dom";

const AddCardDetails = () => {
  const location = useLocation();

  const priceToPay = location.totalPrice;

  const [card, setCard] = useState({
    cardName: "",
    cardNumber: "",
    validThrough: "",
    cvv: "",
  });

  const handleCardInput = (e) => {
    setCard({ ...card, [e.target.name]: e.target.value });
  };

  const payForOrder = () => {};

  return (
    <div>
      <div class="mt-2 d-flex aligns-items-center justify-content-center">
        <div class="card form-card border-color" style={{ width: "25rem" }}>
          <div className="card-header bg-color">
            <h5 class="card-title">Payment Details</h5>
          </div>
          <div class="card-body">
            <form>
              <div class="mb-3">
                <label for="name" class="form-label">
                  Name on Card
                </label>
                <input
                  type="text"
                  class="form-control"
                  id="name"
                  name="cardName"
                  onChange={handleCardInput}
                  value={card.cardName}
                />
              </div>
              <div class="mb-3">
                <label for="cardNumber" class="form-label">
                  Card Number
                </label>
                <input
                  type="text"
                  class="form-control"
                  id="cardNumber"
                  name="cardNumber"
                  onChange={handleCardInput}
                  value={card.cardNumber}
                />
              </div>

              <div className="row">
                <div class="mb-3">
                  <label for="validThrough" class="form-label">
                    Valid Through
                  </label>
                  <input
                    type="text"
                    class="form-control"
                    id="validThrough"
                    name="validThrough"
                    onChange={handleCardInput}
                    value={card.validThrough}
                  />
                </div>

                <div class="mb-3">
                  <label for="cvv" class="form-label">
                    CVV
                  </label>
                  <input
                    type="text"
                    class="form-control"
                    id="cvv"
                    name="cvv"
                    onChange={handleCardInput}
                    value={card.cvv}
                  />
                </div>
              </div>

              <button
                type="submit"
                class="btn custom-bg text-color"
                onClick={payForOrder}
              >
                Pay &#8377; {priceToPay}/-
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddCardDetails;
