import Carousel from "./Carousel";
import GetAllCategories from "../productComponent/GetAllCategories";
import ProductCard from "../productComponent/ProductCard";
import Pagination from "./Pagination";
import axios from "axios";
import { useState, useEffect } from "react";

const HomePage = () => {
  const [products, setProducts] = useState([]);

  const retrieveAllProducts = async () => {
    const response = await axios.get("http://localhost:8080/api/product/all");
    console.log(response.data);
    return response.data;
  };

  useEffect(() => {
    const getAllProducts = async () => {
      const allProducts = await retrieveAllProducts();
      if (allProducts) {
        console.log("data is present :)");
        setProducts(allProducts);
      }
    };

    getAllProducts();
  }, []);

  console.log(products);

  return (
    <div className="container-fluid">
      <Carousel />
      <div className="mt-2">
        <div className="row">
          <div className="col-sm-2">
            <GetAllCategories />
          </div>
          <div className="col-sm-10">
            <div class="row">
              <div class="col-sm-3">
                {products.map((product) => {
                  return <ProductCard props={product} />;
                })}
              </div>
            </div>

            <div className="mt-2 d-flex justify-content-between">
              <Pagination />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
