import { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const UserLoginForm = () => {
  const [loginRequest, setLoginRequest] = useState({
    emailId: "",
    password: "",
  });

  const [response, setResponse] = useState({});

  const handleUserInput = (e) => {
    setLoginRequest({ ...loginRequest, [e.target.name]: e.target.value });
  };

  const loginAction = () => {
    fetch("http://localhost:8080/api/user/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(loginRequest),
    }).then((result) => {
      console.log("result", result);
      result.json().then((res) => {
        sessionStorage.setItem("active-user", JSON.stringify(res));
        toast("Logged In Successfully!!!");
      });
    });
  };

  return (
    <>
      <div>
        <div className="mt-2 d-flex aligns-items-center justify-content-center">
          <div
            className="card form-card border-color"
            style={{ width: "25rem" }}
          >
            <div className="card-header bg-color text-center">
              <h4 className="card-title">Customer Login</h4>
            </div>
            <div className="card-body">
              <form>
                <div className="mb-3">
                  <label for="emailId" class="form-label custom-bg-text">
                    Email Id
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="emailId"
                    name="emailId"
                    onChange={handleUserInput}
                    value={loginRequest.emailId}
                  />
                </div>
                <div className="mb-3">
                  <label for="password" className="form-label custom-bg-text">
                    Password
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    name="password"
                    onChange={handleUserInput}
                    value={loginRequest.password}
                    autoComplete="on"
                  />
                </div>

                <button
                  type="submit"
                  className="btn custom-bg text-color"
                  onClick={loginAction}
                >
                  Login
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
      <ToastContainer />;
    </>
  );
};

export default UserLoginForm;
