import React, { useState, useEffect } from 'react';
import carousel1 from "../images/carousel_1.jpeg";
import carousel2 from "../images/banner 2.jpg";
import carousel3 from "../images/Banner 01.jpeg.jpg";
import carousel4 from "../images/5image 01.jpg";

const Carousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  const images = [carousel1, carousel4, carousel2, carousel3];

  const nextSlide = () => {
    setActiveIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  useEffect(() => {
    const interval = setInterval(nextSlide, 5000); // Change slide every 5 seconds

    return () => {
      clearInterval(interval); // Clear the interval when the component unmounts
    };
  }, []);

  return (
    <div
      id="carouselExampleCaptions"
      className="carousel slide"
      data-bs-ride="false"
    >
      {/* Rest of your carousel JSX */}
    </div>
  );
};

export default Carousel;
