import GetAllCategories from "../productComponent/GetAllCategories";
import jeans_images from "../images/fitjeans.jpg";
import CategoryNavigator from "../productComponent/CategoryNavigator";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import ProductCard from "../productComponent/ProductCard";

const Product = () => {
  const { productId, categoryId } = useParams();

  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState({
    id: "",
    title: "",
    description: "",
    quantity: "",
    price: "",
    imageName: "",
    category: { id: "", title: "" },
  });

  const retrieveProduct = async () => {
    const response = await axios.get(
      "http://localhost:8080/api/product/id?productId=" + productId
    );

    return response.data;
  };

  useEffect(() => {
    const getProduct = async () => {
      const retrievedProduct = await retrieveProduct();

      setProduct(retrievedProduct);
    };

    const getProductsByCategory = async () => {
      const allProducts = await retrieveProductsByCategory();
      if (allProducts) {
        setProducts(allProducts);
      }
    };

    getProduct();
    getProductsByCategory();
  }, [productId]);

  const retrieveProductsByCategory = async () => {
    const response = await axios.get(
      "http://localhost:8080/api/product/category?categoryId=" + categoryId
    );
    console.log(response.data);
    return response.data;
  };

  return (
    <div className="container-fluid">
      <div class="row">
        <div class="col-sm-2 mt-2">
          <GetAllCategories />
        </div>
        <div class="col-sm-3 mt-2 admin">
          <div class="card">
            <img
              src={"http://localhost:8080/api/product/" + product.imageName}
              style={{
                maxHeight: "500px",
                maxWidth: "100%",
                width: "auto",
              }}
              class="card-img-top rounded mx-auto d-block m-2"
              alt="img"
            />
          </div>
        </div>
        <div class="col-sm-5 mt-2">
          <div class="card">
            <div class="card-header">
              <div className="d-flex justify-content-between">
                <h1>{product.title}</h1>
                <CategoryNavigator
                  item={{
                    id: product.category.id,
                    title: product.category.title,
                  }}
                />
              </div>
            </div>

            <div class="card-body text-left">
              <div class="text-left mt-3">
                <h3
                  style={{
                    color: "chocolate",
                  }}
                >
                  Description :
                </h3>
              </div>
              <h4 class="card-text">{product.description}</h4>
            </div>

            <div class="card-footer">
              <div className="text-center">
                <p>
                  <span>
                    <b
                      style={{
                        color: "green",
                      }}
                    >
                      Price : &#8377;{product.price}
                    </b>
                  </span>

                  <span
                    style={{
                      color: "red",
                    }}
                  >
                    <s>&#8377;1199.00</s>/-
                  </span>
                </p>
              </div>
              <div className="d-flex justify-content-between">
                <div>
                  <form class="row g-3">
                    <div class="col-auto">
                      <input
                        type="number"
                        class="form-control"
                        id="addToCart"
                        placeholder="Enter Quantity..."
                      />
                    </div>
                    <div class="col-auto">
                      <button type="submit" class="btn btn-warning mb-3">
                        Add to Cart
                      </button>
                    </div>
                  </form>
                </div>
                <p class="ml-2">Stock : {product.quantity}</p>
              </div>
            </div>
          </div>
        </div>
        <div class="col-sm-2 mt-2">
          <div className="card">
            <div className="card-header text-center">
              <h4>Reviews</h4>
            </div>

            <div className="card-body"></div>
          </div>
        </div>
      </div>

      <div className="row mt-2">
        <div className="col-sm-2"></div>

        <div className="col-sm-10">
          <h2>Related Products:</h2>
          <div class="row">
            <div class="col-sm-3">
              {products.map((product) => {
                return <ProductCard item={product} />;
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Product;
