import GetAllCategories from "../productComponent/GetAllCategories";
import { useLocation } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

const ReviewPage = () => {
  const location = useLocation();
  const user = JSON.parse(sessionStorage.getItem("active-user"));
  const productImageName = location.state.productImage;
  const productId = location.state.productId;

  const [review, setReview] = useState({
    star: "",
    review: "",
  });

  const handleUserReview = (e) => {
    setReview({ ...review, [e.target.name]: e.target.value });
  };

  const addReview = () => {
    const formData = new FormData();
    formData.append("star", review.star);
    formData.append("review", review.review);
    formData.append("userId", user.id);
    formData.append("productId", productId);

    console.log(formData);
    axios
      .post("http://localhost:8080/api/product/user/review", formData)
      .then((resp) => {
        let result = resp.data.data;
        console.log(result);
        alert("REVIEW ADDED SUCCESSFULLY!!!");
      })
      .catch((error) => {
        console.log("Error", error);
        alert("Error submitting the product review!!!");
      });
  };

  return (
    <div className="container-fluid">
      <div class="row">
        <div class="col-sm-2 mt-2">
          <GetAllCategories />
        </div>
        <div class="col-sm-3 mt-2">
          <div class="card form-card border-color">
            <img
              src={"http://localhost:8080/api/product/" + productImageName}
              style={{
                maxHeight: "500px",
                maxWidth: "100%",
                width: "auto",
              }}
              class="card-img-top rounded mx-auto d-block m-2"
              alt="img"
            />
          </div>
        </div>
        <div class="col-sm-5 mt-2">
          <div class="card form-card border-color">
            <div class="card-header bg-color">
              <h1 className="custom-bg-text">Review Product</h1>
            </div>

            <div className="card-body">
              <form>
                <div className="mb-3">
                  <label className="form-label">Category</label>

                  <select
                    name="star"
                    value={review.star}
                    onChange={handleUserReview}
                    className="form-control"
                  >
                    <option value="">Give Star</option>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                  </select>
                </div>
                <div class="mb-3">
                  <label for="review" class="form-label">
                    Review
                  </label>
                  <textarea
                    class="form-control"
                    id="review"
                    rows="3"
                    placeholder="enter your review.."
                    onChange={handleUserReview}
                    value={review.review}
                  />
                </div>
                <button
                  type="submit"
                  class="btn custom-bg text-color"
                  onClick={addReview}
                >
                  Submit Review
                </button>
              </form>
            </div>
          </div>
        </div>
        <div class="col-sm-2 mt-2">
          <div
            className="card form-card border-color"
            style={{
              height: "30rem",
            }}
          >
            <div className="card-header text-center custom-bg text-color">
              <h4>Reviews</h4>
            </div>

            <div
              className="card-body custom-bg-text"
              style={{
                overflowY: "auto",
              }}
            >
              <b>Jafer</b>
              <p>Jeans is awesome, Loved it!!!</p>
              <b>Afzal</b>
              <p>Loved the product quality.</p>
              <b>Inam</b>
              <p>Comfortable Jeans.</p>
              <b>Sachin</b>
              <p>Jeans is awesome, Loved it!!!</p>
              <b>Pranav</b>
              <p>Loved the product quality.</p>
              <b>Irfan</b>
              <p>Comfortable Jeans.</p>
              <b>Ali</b>
              <p>Jeans is awesome, Loved it!!!</p>
              <b>Rahul</b>
              <p>Loved the product quality.</p>
              <b>Salam</b>
              <p>Comfortable Jeans.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReviewPage;
