import { useState } from "react";

const UserLoginForm = () => {
  const [loginRequest, setLoginRequest] = useState({
    emailId: "",
    password: "",
  });

  const [response, setResponse] = useState({});

  const handleUserInput = (e) => {
    setLoginRequest({ ...loginRequest, [e.target.name]: e.target.value });
  };

  const loginAction = () => {
    fetch("http://localhost:8080/api/user/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(loginRequest),
    }).then((result) => {
      console.warn("result", result);
      result.json().then((res) => {
        sessionStorage.setItem("active-user", JSON.stringify(res));
        console.info("response", res);

        console.log("session  storage: ");
        console.info(JSON.parse(sessionStorage.getItem("active-user")));
      });
    });
  };

  return (
    <div>
      <div class="mt-2 d-flex aligns-items-center justify-content-center">
        <div class="card form-card border-color" style={{ width: "25rem" }}>
          <div className="card-header bg-color text-center">
            <h4 class="card-title">Customer Login</h4>
          </div>
          <div class="card-body">
            <form>
              <div class="mb-3">
                <label for="emailId" class="form-label custom-bg-text">
                  Email Id
                </label>
                <input
                  type="email"
                  class="form-control"
                  id="emailId"
                  name="emailId"
                  onChange={handleUserInput}
                  value={loginRequest.emailId}
                />
              </div>
              <div class="mb-3">
                <label for="password" class="form-label custom-bg-text">
                  Password
                </label>
                <input
                  type="password"
                  class="form-control"
                  id="password"
                  name="password"
                  onChange={handleUserInput}
                  value={loginRequest.password}
                  autoComplete="on"
                />
              </div>

              <button
                type="submit"
                class="btn custom-bg text-color"
                onClick={loginAction}
              >
                Register User
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserLoginForm;
