import { useState, useEffect } from "react";
import axios from "axios";

const SearchOrder = () => {
  const [allOrderData, setAllOrderData] = useState([]);
  const [orderId, setOrderId] = useState("");

  const getAllOrder = async () => {
    const allOrder = await retrieveAllOrder();
    if (allOrder) {
      setAllOrderData(allOrder);
    }
  };

  const retrieveAllOrder = async () => {
    const response = await axios.get(
      "http://localhost:8080/api/user/admin/showorder?orderId=" + orderId
    );
    console.log(response.data);
    return response.data;
  };

  const searchOrderById = (e) => {
    getAllOrder();
    setOrderId("");
    e.preventDefault();
  };

  return (
    <div>
      <div
        className="card form-card mt-1 ms-2 me-2 mb-2"
        style={{
          height: "27rem",
        }}
      >
        <div className="card-header text-center bg-color">
          <h4>Searh Order</h4>
        </div>
        <div
          className="card-body"
          style={{
            overflowY: "auto",
          }}
        >
          <form class="row g-3">
            <div class="col-auto">
              <input
                type="text"
                class="form-control"
                id="inputPassword2"
                placeholder="Enter Order Id..."
                onChange={(e) => setOrderId(e.target.value)}
                value={orderId}
              />
            </div>
            <div class="col-auto">
              <button
                type="submit"
                class="btn custom-bg text-color mb-3"
                onClick={searchOrderById}
              >
                Search
              </button>
            </div>
          </form>
          <div className="table-responsive">
            <table className="table table-hover custom-bg-text text-center">
              <thead className="bg-color table-bordered border-color">
                <tr>
                  <th scope="col">Order Id</th>
                  <th scope="col">Product</th>
                  <th scope="col">Name</th>
                  <th scope="col">Description</th>
                  <th scope="col">Quantity</th>
                  <th scope="col">Total Price</th>
                  <th scope="col">Customer Name</th>
                  <th scope="col">Street</th>
                  <th scope="col">City</th>
                  <th scope="col">Pin code</th>
                  <th scope="col">Mobile No.</th>
                  <th scope="col">Order Date</th>
                  <th scope="col">Delivery Date</th>
                  <th scope="col">Delivery Status</th>
                </tr>
              </thead>
              <tbody>
                {allOrderData.map((orderData) => {
                  return (
                    <tr>
                      <td>{orderData.orderId}</td>
                      <td>
                        <img
                          src={
                            "http://localhost:8080/api/product/" +
                            orderData.productImage
                          }
                          class="img-fluid"
                          alt="product_pic"
                          style={{
                            maxWidth: "90px",
                          }}
                        />
                      </td>
                      <td>{orderData.productName}</td>
                      <td>{orderData.productDescription}</td>
                      <td>{orderData.quantity}</td>
                      <td>{orderData.totalPrice}</td>
                      <td>{orderData.userName}</td>
                      <td>{orderData.address.street}</td>
                      <td>{orderData.address.city}</td>
                      <td>{orderData.address.pincode}</td>
                      <td>{orderData.userPhone}</td>
                      <td>{orderData.orderDate}</td>
                      <td>{orderData.deliveryDate}</td>
                      <td>{orderData.deliveryStatus}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div>
        <div className="card form-card ms-2 me-2 mb-5">
          <div className="card-header text-center bg-color">
            <h4>Update Delivery Status</h4>
          </div>
          <div className="card-body">
            <form class="row g-3">
              <div class="col-auto">
                <label>Order Id</label>
                <input
                  type="text"
                  class="form-control"
                  id="inputPassword2"
                  placeholder="Enter Order Id..."
                />
              </div>
              <div class="col-auto">
                <input
                  type="date"
                  class="form-control"
                  id="inputPassword2"
                  placeholder="Enter Delivery Date..."
                />
              </div>
              <div class="col-auto">
                <input
                  type="text"
                  class="form-control"
                  id="inputPassword2"
                  placeholder="Enter Delivery Time..."
                />
              </div>
              <div class="col-auto">
                <input
                  type="text"
                  class="form-control"
                  id="inputPassword2"
                  placeholder="Select Delivery Status..."
                />
              </div>
              <div class="col-auto">
                <button type="submit" class="btn custom-bg text-color mb-3">
                  Update Delivery Status
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SearchOrder;
